import React, { useState } from 'react';
import Switch from 'react-switch';
import TimePicker from 'react-time-picker';

const DayRow = ({ day }) => {

    const { t } = useTranslation();

    const [isOpen, setIsOpen] = useState(false);
    const [fromTime, setFromTime] = useState(t('default_from_time'));
    const [toTime, setToTime] = useState(t('default_to_time'));

    return (
        <div className="day-row">
            <span className="day-label">{day}</span>
            <Switch isOn={isOpen} handleToggle={() => setIsOpen(!isOpen)} />

            <TimePicker onChange={setFromTime} value={fromTime} disabled={!isOpen} />
            <TimePicker onChange={setToTime} value={toTime} disabled={!isOpen} />
        </div>
    );
};

export default DayRow;
