import React from "react";
import Dropdown from "./Dropdown";


const MenuItem = ({ item, dropdown, userRoles }) => {

    const hasPermission = (item) => { return userRoles.some(role => item.roles.includes(role)); }

    return (
        <>
            {hasPermission(item) ? (

                item.submenu ? (
                    <>
                        <a className="menu-item"
                            aria-haspopup="menu"
                            aria-expanded={dropdown ? "true" : "false"}
                            href={item.url}
                            style={item.url ? { cursor: "pointer" } : { cursor: "default" }}
                        >
                            {item.title}
                        </a><Dropdown submenus={item.submenu} dropdown={dropdown} /></>
                ) : (
                    <a className='menu-item' href={item.url}>{item.title}</a>
                )) :
                null
            }
        </>
    )
}

export default MenuItem;
