export const langData = [
    {
        code: 'de',  // needs to be first in list, since de is the default display language
        label: 'DE',
    },
    {
        code: 'en',
        label: 'EN',
    },
]

export const getLanguageDataByCode = (code) => {

    return languageData.find(lang => {
        return lang.code === code
    })
}
