import React, { useState } from "react";
import { useTranslation } from 'react-i18next';

import "./PictureDescriptionEdit.css"

const PictureDescriptionEdit = () => {

    const { t } = useTranslation();

    const [pictureFile, setPictureFile] = useState(null)

    function handlePictureFileChange(event) {
        setPictureFile(event.target.files[0])
    }

    const handleImgContainerClick = () => {
        document.getElementById('pictureFileInput').click();
    };

    const handleRemovePictureClick = () => {
        setPictureFile(null)
    }

    // prevents user from switching to new line by hitting [Enter]
    const handleAddressKeyDown = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    };


    return (
        <>
            <div className="Switchimage-and-desctiption-container">

                <div className="Switchimg-container" type="file" onClick={handleImgContainerClick}>
                    {pictureFile ?
                        <img className="Switchimg" alt={t('store_image')} src={URL.createObjectURL(pictureFile)}></img>
                        :
                        <span className="Switchimg-container-label">{t('select_a_picture')}</span>
                    }
                </div>

                <input
                    id="pictureFileInput"
                    type="file"
                    title={t('image-selection')}
                    accept="image/*"
                    onChange={handlePictureFileChange}
                    style={{ display: 'none' }}
                />

                <textarea title={t('store_description')} className="Switchdescription" placeholder={t('store_description')}></textarea>
            </div>

            <div className="button-container">
                <button type="button" className="button regular" onClick={handleRemovePictureClick}>{t('remove_picture')}</button>
            </div>
        </>
    );
};

export default PictureDescriptionEdit;
