import i18n from "i18next";
import { getLangDataByCode, langData, defaultLang } from "../../utils/languageToggle";
import React, { useEffect, useState } from "react";

const ThemeToggle = () => {


    const defaultDark = window.matchMedia('(prefers-color-sceme: dark)').matches
    const [theme, setTheme] = useLocalStorage('theme', defaultDark ? 'dark' : 'light')

    const switchTheme = () => {
        const newTheme = theme === 'light' ? 'dark' : 'light';
        setTheme(newTheme);
    }

    return (
        <button className='toggle theme' onClick={switchTheme}>{currLang.label}</button>
    );
}

export default ThemeToggle;
