import React, { useEffect, useState } from 'react';
import { menuItems, getAccountItemByRoles } from '../../data/menuData'
import MenuItems from './MenuItems';
import MenuItem from './MenuItem';
import './Navbar.css';
import logo from '../../assets/images/logo.png';
import { USER_DATA } from '../../constants/URIs/UserURIs';
import { getCookie } from '../../services/Cookies';
import { ERR_FETCHING_USER_DATA } from '../../constants/ErrorMessages';

const Navbar = () => {

    // const userRoles = ['admin']; // TODO fetch

    const token = getCookie('token');

    // default role is visitor (not logged in)
    const [userRoles, setUserRoles] = useState(['visitor']);
    const [filteredMenuItems, setFilteredMenuItems] = useState([]);

    const fetchUserRoles = () => {
        fetch(USER_DATA, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Token ${token}`,
            }
        })
            .then(response => response.json())
            .then(data => {
                setUserRoles(data.user_flags.map(element => element.flag));
            })
            .catch(error => {
                console.error(ERR_FETCHING_USER_DATA, error);
            });
    }

    useEffect(() => {
        fetchUserRoles();
    }, []);
    useEffect(() => {
        setFilteredMenuItems(
            menuItems.map((item, index) => {
                if (userRoles.some(role => item.roles.includes(role))) return (item, index)
            }));
    }, [userRoles, filteredMenuItems]);

    return (
        <div className='nav-area'>
            <a href='/' className='logo-container'>
                <img className='logo-img' src={logo} alt='Logo' />
            </a>
            <nav className='nav'>
                <ul className='menus'>
                    {console.log(menuItems)}
                    {filteredMenuItems.length > 0 ? filteredMenuItems.map((item, index) => (
                        <MenuItems key={index} item={item} userRoles={userRoles} />
                    )) : null}
                    <li>
                        <MenuItem className='account-menu-item' item={getAccountItemByRoles(userRoles)} userRoles={userRoles} />
                    </li>
                </ul>
            </nav>
        </div>
    );
}

export default Navbar;
