import React, { useState, startTransition } from 'react';
import { useTranslation } from 'react-i18next';

import { useNavigate } from 'react-router-dom';

const Enrollment = () => {

    const { t } = useTranslation();

    const navigate = useNavigate();

    const [username, setUsername] = useState();
    const [email, setEmail] = useState();

    // this prevents user from switching to new line by hitting [Enter]
    const handleFieldKeyDown = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    };

    return (
        <>
            <h1>{t('enrollment')}</h1>

            <p>{t('enter_one_of_the_following_to_identify_user')}</p>

            <textarea
                title={t('enter_username')}
                className='username'
                rows='1'
                value={username}
                onChange={e => setUsername(e.target.value)}
                onKeyDown={handleFieldKeyDown}
                placeholder={t('enter_username')}
            >
            </textarea>

            <textarea
                title={t('enter_email')}
                className='email'
                rows='1'
                value={email}
                onChange={e => setEmail(e.target.value)}
                onKeyDown={handleFieldKeyDown}
                placeholder={t('enter_email')}
            >
            </textarea>

            <select title='stores' className='select' onChange={handleStoreSelect}>
                {stores.map(e => <option key={e.key} value={e.key}>{e.value}</option>)};
            </select>
        </>
    );
};

export default Enrollment;
