import React, { useState, startTransition } from 'react';
import { useTranslation } from 'react-i18next';

import { useNavigate } from 'react-router-dom';

const roles = [
    { key: 1, value: 'admin' },
    { key: 2, value: 'Manager of \'Store 1\'' },
    { key: 3, value: 'Manager of \'Store 2\'' },
    { key: 4, value: 'Manager of \'Store 3\'' },
    { key: 5, value: 'Manager of \'Store 4\'' }
]

const Enrollment = () => {

    const { t } = useTranslation();

    const navigate = useNavigate();

    const [username, setUsername] = useState();
    const [email, setEmail] = useState();

    const [role, setRole] = useState();

    const handleRoleSelect = (e) => {
        setRole(e.target.value)
    }

    // this prevents user from switching to new line by hitting [Enter]
    const handleFieldKeyDown = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    };

    return (
        <>
            <h1>{t('enrollment')}</h1>

            <p>{t('enter_one_of_the_following_to_identify_user')}</p>

            <textarea
                title={t('enter_username')}
                className='username'
                rows='1'
                value={username}
                onChange={e => setUsername(e.target.value)}
                onKeyDown={handleFieldKeyDown}
                placeholder={t('enter_username')}
            >
            </textarea>

            <textarea
                title={t('enter_email')}
                className='email'
                rows='1'
                value={email}
                onChange={e => setEmail(e.target.value)}
                onKeyDown={handleFieldKeyDown}
                placeholder={t('enter_email')}
            >
            </textarea>

            {/* TODO style */}

            <p>{t('select_role')}</p>
            <select title='roles' className='select' onChange={handleRoleSelect}>
                {roles.map(e => <option key={e.key} value={e.key}>{e.value}</option>)};
            </select>

            <div className='button-container'>
                <button type='button' className='button accent' onClick={handleEnrollClick}>{t('enroll')}</button>
            </div>
        </>
    );
};

export default Enrollment;
