import React, { useEffect, useState } from 'react';
import { menuItems, getAccountItemByRoles } from '../../data/menuData'
import MenuItems from './MenuItems';
import MenuItem from './MenuItem';
import './Navbar.css';
import logo from '../../assets/images/logo.png';
import { USER_DATA } from '../../constants/URIs/UserURIs';
import { getCookie } from '../../services/Cookies';
import { ERR_FETCHING_USER_DATA } from '../../constants/ErrorMessages';

const Navbar = () => {

    // const userRoles = ['admin']; // TODO fetch

    const token = getCookie('token');

    const [userRoles, setUserRoles] = useState([]);

    const fetchUserRoles = async () => {
        try {
            const response = await fetch(USER_DATA, {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Token ${token}`,
                }
            });

            if (!response.ok) {
                throw new Error('Failed to fetch user roles');
            }

            const data = await response.json();
            setUserRoles(data.user_flags.map(element => element.flag));
        } catch (error) {
            console.error(ERR_FETCHING_USER_DATA, error);
        }
    }

    useEffect(() => {
        fetchUserRoles();
        console.log("token (Navbar)", token);
        console.log("userRoles (Navbar)", userRoles);
    },[token]);

    const accountItem = getAccountItemByRoles(userRoles);

    return (
            <div className='nav-area'>
                <a href='/' className='logo-container'>
                    <img className='logo-img' src={logo} alt='Logo'/>
                </a>
                <nav className='nav'>
                    <ul className='menus'>
                        {menuItems.map((item, index) => {
                            return <MenuItems item={item} key={index} userRoles={userRoles}/>;
                        })}
                        <li>
                            {console.log("userRoles (Navbar)", userRoles)}
                            <MenuItem className='account-menu-item' item={accountItem} userRoles={userRoles}/>
                        </li>
                    </ul>
                </nav>
            </div>
    );
}

export default Navbar;
