import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

import PictureAndDescriptionField from '../../../components/IO/PictureAndDescriptionField';
import AddressField from '../../../components/IO/AddressField';
import { useParams } from 'react-router-dom';
import { Descriptions } from 'antd';

import image from '../../../assets/images/store1.jpg'

// TODO implement fetching
let stores = [
    {
        id: 1,
        name: 'Store 1',
        image: require('../../../assets/images/store1.jpg').default,
        description: "This is a description of Store 1",
        address: "Musterstraße 123, 76137 Karlsruhe"
    },
    {
        id: 2,
        name: 'Store 2',
        image: require('../../../assets/images/store1.jpg').default,
        description: "This is a description of Store 2",
        address: "Musterstraße 123, 76137 Karlsruhe"
    },
    {
        id: 3,
        name: 'Store 3',
        image: null,
        description: "This is a description of Store 3",
        address: "Musterstraße 123, 76137 Karlsruhe"
    }
]

const StorePage = () => {

    const { t } = useTranslation();

    const { id } = useParams();
    const store = stores.find(s => s.id === parseInt(id));

    // REMOVE
    // const pictureFile = store.image
    const editable = false
    const [pictureFile, setPictureFile] = useState(store.image)
    const [description, setDescription] = useState(store.description)

    console.log(pictureFile)

    function handlePictureFileChange(event) {
        setPictureFile(event.target.files[0])
    }

    const handleImgContainerClick = () => {
        document.getElementById('pictureFileInput').click();
    };

    const handleRemovePictureClick = () => {
        setPictureFile(null)
    }

    return (
        <>
            <h1>{store.name}</h1>

            <div className="img-container" type="file" onClick={handleImgContainerClick}>
                    {pictureFile ?
                        <img className="img" alt={t('image')} src={URL.createObjectURL(pictureFile)}></img>
                        :
                        <span className="img-container-label">{t('select_a_picture')}</span>
                    }
                </div>

                <input
                    id="pictureFileInput"
                    type="file"
                    title={t('image-selection')}
                    accept="image/*"
                    onChange={handlePictureFileChange}
                    style={{ display: 'none' }}
                    disabled={!editable}
                />

            <PictureAndDescriptionField editable={false} store={store}/>
            <AddressField editable={false} store={store}/>
        </>
    );
};

export default StorePage;
