import React, { useEffect, useRef } from "react";
import { menuData } from "../../data/menuData"
import MenuItems from "./MenuItems";
import "./Navbar.css";
import logo from "../../assets/images/logo.png";
import { useState } from "react";


const Navbar = () => {

    const [isHovered, setIsHovered] = useState(false);

    // useEffect(() => {
    //     if (mouseOver) {
    //         console.log("true")
    //         console.log(btn.current.style.marginBottom)
    //         console.log(`${dropdown.current.offsetHeight}px`)
    //         btn.current.style.marginBottom = `${dropdown.current.offsetHeight}px`;
    //     } else btn.current.style.marginBottom = 0;
    // }, [mouseOver]);

    // function handleMouseEnter() {
    //     setHover(true);
    // }

    // function handleMouseLeave() {
    //     setHover(false);
    // }

    return (
        <header>
            <div class="nav-area">
                <a href="/" class="logo">
                    <img class="logo-img" src={logo} alt="Logo" />
                </a>
                <nav class="nav">
                    <li class="menus">
                        {menuData.map((menu, index) => (
                            <div class="dropdown-container" key={index}
                                onMouseEnter={() => setIsHovered(true)}
                                onMouseLeave={() => setIsHovered(false)}>
                                <div class="link">
                                    <a href={menu.url}>{menu.title}</a>
                                </div>
                                {menu.submenu && isHovered ? (
                                    <ul class="dropdown">
                                        {menu.submenu.map((menu, index) => (
                                            <li key={index} class="dropdown-items">
                                                <a href={menu.url}>{menu.title}</a>
                                            </li>
                                        ))}
                                    </ul>
                                ) : null
                                }
                            </div>
                        ))}
                    </li>
                </nav>
            </div>
        </header>
    )

    return (
        <header>
            <div class="nav-area">
                <a href="/" class="logo">
                    <img class="logo-img" src={logo} alt="Logo" />
                </a>
                <nav class="nav">
                    <ul class="menus">
                        {menuData.map((menu, index) => {
                            return <MenuItems items={menu} key={index} />;
                        })}
                    </ul>
                </nav>
            </div>
        </header>
    );
}

export default Navbar;
