import React, { useState } from "react";
import { useTranslation } from 'react-i18next';

import "./PictureDescriptionEdit.css"

const AddressEdit = () => {

    const { t } = useTranslation();

    // prevents user from switching to new line by hitting [Enter]
    const handleAddressKeyDown = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    };

    return (
        <textarea title={t('address')} className="address" rows="1" onKeyDown={handleAddressKeyDown} placeholder={t('address')}></textarea>
    );
}

export default AddressEdit;
