import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

import { LOGIN } from '../constants/URIs/UserURIs';

const Login = () => {

    const { t } = useTranslation();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const [token, setToken] = useState()

    function handleLoginClick() {
        postLogin();
        setTokenCookie();
        navigateToNextPage();
    }

    // POST login request
    const postLogin = () => {

        let payload = {
            username: username,
            password: password
        };

        // POST login request
        fetch(LOGIN, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(payload)
        })
            .then(response => {
                if (response?.ok) {
                    return response.json();
                } else {
                    // If the request was not successful, throw an error
                    return response.json().then(errorData => {
                        throw new Error(errorData.message);
                    });
                }
            })
            .then(data => {
                setToken(data.token);
            })
            .catch(error => {
                // Handle any network or other errors that occurred during the request
                alert('Error making login request.' + error.message);
            });
    }

    const handleHelmholtzLoginClick = () => {
        // TODO use proper variable
        window.location.replace(URL_USER_HELMHOLTZ);
    }

    const handleRegisterClick = () => {
        // TODO navigate to register page
    }

    const setTokenCookie = () => {
        var days = 1
        const expirationDate = new Date();
        expirationDate.setDate(expirationDate.getDate() + days);
        document.cookie = `${'token'}=${token}; expires=${expirationDate.toUTCString()}; path=/`;
    };

    // this prevents user from switching to new line by hitting [Enter]
    const handleFieldKeyDown = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    };

    const navigateToNextPage = () => {
        // TODO implement and account for different locations from which user can log in and navigate back to them
    }

    return (
        <>
            {/* THINK about style */}
            <h1>{t('login')}</h1>

            <textarea
                title={t('enter_username')}
                className='username'
                rows='1'
                value={username}
                onChange={e => setUsername(e.target.value)}
                onKeyDown={handleFieldKeyDown}
                placeholder={t('enter_username')}
            >
            </textarea>

            {/* TODO check if there is an extra textarea type for passwords */}
            <textarea
                title={t('enter_password')}
                className='password'
                rows='1'
                value={password}
                onChange={e => setPassword(e.target.value)}
                onKeyDown={handleFieldKeyDown}
                placeholder={t('enter_password')}
            >
            </textarea>

            <button onClick={handleLoginClick}>{t('submit')}</button>
            <button onClick={handleHelmholtzLoginClick}>{t('helmholtz_login')}</button>
            <button onClick={handleRegisterClick}>{t('register_instead')}</button>
        </>
    );
};

export default Login;
