import React, { useState } from 'react';


const Login = () => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('')

    function handleLoginClick() {
        postLogin();
    }

    // POST login request
    const postLogin = () => {

        let payload = {
            username: username,
            password: password
        };

        // POST login request
        fetch(URI_USER_LOGIN, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(payload)
        })
            .then(response => {
                if (response?.ok) {
                    return response.json();
                } else {
                    // If the request was not successful, throw an error
                    return response.json().then(errorData => {
                        throw new Error(errorData.message);
                    });
                }
            })
            .then(data => {
                const token = data.token;
                navigateToNextPage(token);
            })
            .catch(error => {
                // Handle any network or other errors that occurred during the request
                alert('Error making login request.' + error.message);
            });
    }

    const handeHelmholtzSignInClick = () => {
        window.location.replace(URL_USER_HELMHOLTZ);
    }

    const handleSignInClick = () => {
        // navigate to register page
    }

    const setTokenCookie = () => {
        var days = 1
        const expirationDate = new Date();
        expirationDate.setDate(expirationDate.getDate() + days);
        document.cookie = `${'token'}=${token}; expires=${expirationDate.toUTCString()}; path=/`;
    };

    return (
        <>
            <h1>Login</h1>

            <textarea
                title={t('enter_username')}
                className='username'
                rows='1'
                value={username}
                onChange={e => setUsername(e.target.value)}
                onKeyDown={handleUSernameFieldKeyDown}
                placeholder={t('enter_username')}
            >
            </textarea>

            <input type='text' value={username} onChange={e => setUsername(e.target.value)}></input>
            <button onClick={() => setUsernameCookie()}>Submit</button>
        </>
    );
};

export default Login;
