import React, { useState } from 'react';
import Switch from '../../../components/switch/Switch';
import TimePicker from 'react-time-picker';
import 'react-time-picker/dist/TimePicker.css';
import '../../../components/timePicker/TimePickerCustom.css'

import { useTranslation } from 'react-i18next';
import { MdOutlineSignalWifiStatusbarConnectedNoInternet4 } from 'react-icons/md';

const DayRow = ({ day }) => {

    const { t } = useTranslation();

    const [isOpen, setIsOpen] = useState(false);
    const [isClosed, setIsClosed] = useState(false)
    // const [fromTime, setFromTime] = useState(t('default_from_time'));
    // const [toTime, setToTime] = useState(t('default_to_time'));
    const [openFromTime, setOpenFromTime] = useState('08:00');
    const [openToTime, setOpenToTime] = useState('19:00');
    const [closedFromTime, setClosedFromTime] = useState('12:00');
    const [closedToTime, setClosedToTime] = useState('13:00');

    const handleOpenToggle = () => {
        if (isOpen) {
            setIsClosed(false)
        }
        setIsOpen(!isOpen)
    }

    return (
        <div className='day-row'>
            <span className="day-label">{day}</span>
            <Switch className='opening-times-toggle' id={`${day}-open`} isOn={isOpen} handleToggle={() => setIsOpen(!isOpen)} disabled={false}/>
            <div className='time-picker-container'>
                <TimePicker
                    className='time-picker'
                    id={`${day}-open-from`}
                    onChange={setOpenFromTime}
                    value={openFromTime}
                    disabled={!isOpen}
                />
            </div>
            <div className='time-picker-container'>
                <TimePicker
                    className='time-picker'
                    id={`${day}-open-to`}
                    onChange={setOpenToTime}
                    value={openToTime}
                    disabled={!isOpen}
                />
            </div>
            <Switch className='opening-times-toggle' id={`${day}-closed`} isOn={isClosed} handleToggle={() => setIsClosed(!isClosed)} disabled={!isOpen}/>
            <div className='time-picker-container'>
                <TimePicker
                    className='time-picker'
                    id={`${day}-closed-from`}
                    onChange={setClosedFromTime}
                    value={closedFromTime}
                    disabled={!isClosed}
                />
            </div>
            <div className='time-picker-container'>
                <TimePicker
                    className='time-picker'
                    id={`${day}-closed-to`}
                    onChange={setClosedToTime}
                    value={closedToTime}
                    disabled={!isClosed}
                />
            </div>
        </div>
    );
};

export default DayRow;
