import React, { useState } from "react";
import { useTranslation } from 'react-i18next';

import "./StoreRegistration.css"

const StoreRegistration = () => {

    const { t } = useTranslation();

    const [pictureSelected, setPictureSelected] = useState(false);

    document.getElementById('new-store-address').addEventListener('keydown', function (event) {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    });

    return (
        <>
            <h1>{t('new_store')}</h1>

            <div>
                <div className="new-store-img-container">
                    {pictureSelected ?
                        <img className="new-store-img" alt={t('store_image')} src="source"></img>
                        :
                        <p>{t('select_a_picture')}</p>
                    }
                </div>

                <textarea title={t('store_description')} className="new-store-description"></textarea>
            </div>

            <textarea title={t('store_address')} className="new-store-address" rows="1" multiline="False"></textarea>

            <div className="button-container">
                <button type="button" className="button picture">{t('remove_picture')}</button>
                <button type="button" className="button register">{t('register_new_store')}</button>
            </div>
        </>
    );
};

export default StoreRegistration;
