import React, { useEffect, useRef } from "react";
import { menuData } from "../../data/menuData"
import MenuItems from "./MenuItems";
import "./Navbar.css";
import logo from "../../assets/images/logo.png";
import { useState } from "react";


const Navbar = () => {

    const [mouseOver, setMouseOver] = useState(false);
    const dropdown = useRef();
    const btn = useRef();

    // useEffect(() => {
    //     if (mouseOver) {
    //         console.log("true")
    //         console.log(btn.current.style.marginBottom)
    //         console.log(`${dropdown.current.offsetHeight}px`)
    //         btn.current.style.marginBottom = `${dropdown.current.offsetHeight}px`;
    //     } else btn.current.style.marginBottom = 0;
    // }, [mouseOver]);

    function handleMouseOver() {
        setMouseOver(!mouseOver);
    }

    return (
        <header>
            <div className="nav-area">
                <a href="/" className="logo">
                    <img className="logo-img" src={logo} alt="Logo" />
                </a>
                <nav className="nav">
                    <li className="menus">
                        {menuData.map((menu, index) => (
                            <div key={index}>
                                <div className="link" onMouseOver={handleMouseOver} ref={btn}>
                                    <a href={menu.url}>{menu.title}</a>
                                </div>
                                {menu.submenu ? (
                                    <li className={"dropdown-content"} ref={dropdown}>
                                        {menu.submenu.map((menu, index) => (
                                            <li key={index} className="dropdown-items">
                                                <a href={menu.url}>{menu.title}</a>
                                            </li>
                                        ))}
                                    </li>
                                ) : null
                                }
                            </div>
                        ))}
                    </li>
                </nav>
            </div>
        </header>
    )

    return (
        <header>
            <div className="nav-area">
                <a href="/" className="logo">
                    <img className="logo-img" src={logo} alt="Logo" />
                </a>
                <nav className="nav">
                    <ul className="menus">
                        {menuData.map((menu, index) => {
                            return <MenuItems items={menu} key={index} />;
                        })}
                    </ul>
                </nav>
            </div>
        </header>
    );
}

export default Navbar;
