export const langData = [
    {
        code: 'de',
        label: 'DE',
    },
    {
        code: 'en',
        label: 'EN',
    },
]

export const getLangDataByCode = (code) => {

    lang = langData.find(lang => {
        return lang.code === code
    })

    return lang ? lang : langData[0]
}
