import { React, useEffect, useRef } from 'react';
import Dropdown from './Dropdown';
import { useState } from 'react';
import "./Navbar.css";

const MenuItems = ({ items }) => {

    const [mouseOver, setMouseOver] = useState(false);
    const dropdown = useRef();
    const btn = useRef()

    useEffect(() => {
        if (mouseOver)
            btn.current.style.marginBottom = `${dropdown.current.offsetHeight}px`;
        else btn.current.style.marginBottom = 0;
    }, [mouseOver]);

    function handleMouseOver() {
        setMouseOver(!mouseOver);
    }

    return (
        <li className="menu-items">
            <div className="link" onMouseOver={handleMouseOver}>
                <a href={items.url}>{items.title}</a>
            </div>
            {hover ? (
                <div className="dropdown-content" ref={dropdown}>
                    {items.submenu.map((submenu, index) => (
                        <li key={index} className="dropdown-items">
                            <a href={submenu.url}>{submenu.title}</a>
                        </li>
                    ))}
                </div>
            ) : null}
        </li>
    )

    return (
        <li className="menu-items"
            onMouseEnter={() => setDropdown(true)}
            onMouseLeave={() => setDropdown(false)}
        >
            {items.submenu ? (
                <>
                    <div className="link">
                        <a href={items.url}>{items.title}</a>
                    </div>
                    <ul className={`dropdown ${dropdown ? "show" : "hide"}`}>
                        {items.submenu.map((submenu, index) => (
                            <li key={index} className="dropdown-items">
                                <a href={submenu.url}>{submenu.title}</a>
                            </li>
                        ))}
                    </ul>
                </>
            ) : (
                <div className='link'>
                    <a href={items.url}>{items.title}</a>
                </div>
            )}
        </li>
    );
};

export default MenuItems;
