import React from 'react';
import useLocalStorage from 'use-local-storage';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { ProtectedRoute } from './utils/ProtectedRoute';

import './App.css'

import { MdLightMode } from "react-icons/md";
import { MdDarkMode } from "react-icons/md";

import Home from './pages/Home';
import Login from './pages/Login';
import Navbar from './components/navbar/Navbar';
import Booking from './features/booking/pages/Booking';
import StoreList from './features/storeList/pages/StoreList';
import NoPermission from './pages/NoPermission';

import i18n from './i18n';

const App = () => {

    const defaultDark = window.matchMedia('(prefers-color-sceme: dark)').matches
    const [theme, setTheme] = useLocalStorage('theme', defaultDark ? 'dark' : 'light')

    const switchTheme = () => {
        const newTheme = theme === 'light' ? 'dark' : 'light';
        setTheme(newTheme);
    }

    const switchLangauge = () => {

    }

    const resources = {
        en: {
            translation: translationEN,
        },
        fr: {
            translation: translationFR,
        },
        es: {
            translation: translationES,
        },
    };

    i18n.use(initReactI18next).init({
        resources,
        lng: "en",
        fallbackLng: "en",
        interpolation: {
            escapeValue: false,
        },
    });

    // const userRoles = fetchUserRoles(); TODO: implement all the necessary fetching
    const userRoles = ['admin']

    // TODO: create section for darkmode toggle and language menu in navbar

    return (
        <div className='App' data-theme={theme}>
            <BrowserRouter>
                <Navbar />
                <Routes>
                    <Route exact path='/' element={<Home />} />
                    <Route exact path='/login' element={<Login />} />
                    <Route exact path='/rent' element={<Booking />} />
                    <Route exact path='/store-management' element={<ProtectedRoute routeRoles={['manager', 'admin']} userRoles={userRoles} />}>
                        <Route exact path='/store-management' element={<StoreList />} />
                    </Route>
                    <Route exact path='/no-permission/' element={<NoPermission />} />
                </Routes>
                <div className='side-panel'>
                    <button className='language-toggle' onClick={switchLangauge}>DE</button>
                    <button className='dark-theme-toggle' onClick={switchTheme}>{theme === 'light' ? <MdDarkMode /> : <MdLightMode />}</button>
                </div>
            </BrowserRouter>
        </div>
    );
}

export default App;
