import React, { useState } from 'react';
import Switch from '../../../components/switch/Switch';
import TimePicker from 'react-time-picker';
import 'react-time-picker/dist/TimePicker.css';
import '../../../components/timePicker/TimePickerCustom.css'

import { useTranslation } from 'react-i18next';

const DayRow = ({ day }) => {

    const { t } = useTranslation();

    const [isOpen, setIsOpen] = useState(false);
    const [isClosedInbetween, setIsClosedInbetween] = useState(false)
    // const [fromTime, setFromTime] = useState(t('default_from_time'));
    // const [toTime, setToTime] = useState(t('default_to_time'));
    const [openFromTime, setOpenFromTime] = useState('08:00');
    const [openToTime, setOpenToTime] = useState('19:00');
    const [closedInbetweenFromTime, setClosedInbetweenFromTime] = useState('12:00');
    const [closedInbetweenToTime, setClosedInbetweenToTime] = useState('13:00');

    return (
        <div className='day-row'>
            <span className="day-label">{day}</span>
            <Switch className='opening-times-toggle' id={day} isOn={isOpen} handleToggle={() => setIsOpen(!isOpen)} />
            <div className='time-picker-container'>
                <TimePicker
                    className='time-picker'
                    id={`${day}-open-from`}
                    onChange={setOpenFromTime}
                    value={openFromTime}
                    disabled={!isOpen}
                />
            </div>
            <div className='time-picker-container'>
                <TimePicker
                    className='time-picker'
                    id={`${day}-open-to`}
                    onChange={setOpenToTime}
                    value={openToTime}
                    disabled={!isOpen}
                />
            </div>
            <Switch className='opening-times-toggle' id={day} isOn={isClosedInbetween} handleToggle={() => setIsClosedInbetween(!isClosedInbetween)} />
            <div className='time-picker-container'>
                <TimePicker
                    className='time-picker'
                    id={`${day}-closed-from`}
                    onChange={setClosedInbetweenFromTime}
                    value={closedInbetweenFromTime}
                    disabled={!isClosedInbetween}
                />
            </div>
            <div className='time-picker-container'>
                <TimePicker
                    className='time-picker'
                    id={`${day}-closed-to`}
                    onChange={setClosedInbetweenToTime}
                    value={closedInbetweenToTime}
                    disabled={!isClosedInbetween}
                />
            </div>
        </div>
    );
};

export default DayRow;
