import React, { useState, startTransition } from 'react';
import { useTranslation } from 'react-i18next';

import { useNavigate } from 'react-router-dom';

// const roles = [
//     { key: 1, value: 'admin' },
//     { key: 2, value: 'Manager of \'Store 1\'' },
//     { key: 3, value: 'Manager of \'Store 2\'' },
//     { key: 4, value: 'Manager of \'Store 3\'' },
//     { key: 5, value: 'Manager of \'Store 4\'' }
// ]

const Enrollment = () => {

    const { t } = useTranslation();

    const navigate = useNavigate();

    const [username, setUsername] = useState();
    const [email, setEmail] = useState();

    const [stores, setStores] = useState([]);
    const [selectedRole, setSelectedRole] = useState({});

    // TODO token acquisition
    const [token, setToken] = useState()

    const fetchStores= () => {
        // TODO proper URI variable
        fetch('URI_ADMIN_STORES', {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Token ${token}`
            },
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                setStores(data)
            })
    }
    useEffect(() => {
        fetchStores();
    }, [])

    const roleOptions = [...stores.map((store) => (
        { value: 'Store: ' + store.name, label: 'Manager: ' + store.name }
    )), { value: 'Administrator', label: 'Admin' }];

    const postEnrollment = () => {

        let payload = {
            contact_data: email,
            user_status: selectedRole
        };

        // POST enrollment request
        fetch(URI_ADMIN_ENROLL, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Token ${token}`
            },
            body: JSON.stringify(payload)
        })
            .then(response => {
                if (response) {
                    alert(t('enrollment_successful'));
                    navigateToNextPage();
                }
                else {
                    return response.json().then((errorText) => {
                        throw new Error(errorText.detail);
                    });
                }
            })
            .catch(error => {
                alert(ERR_POSTING_ENROLLMENT + ' ' + error.message);
            })
    }

    const handleRoleChange = (selectedRoleOption) => {
        setSelectedRole(selectedRoleOption.value)
    }

    // this prevents user from switching to new line by hitting [Enter]
    const handleFieldKeyDown = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
        }
    };

    const handleEnrollClick = () => {
        postEnrollment()
        // TODO notify on success/failure perhaps via alert (in this line)
        // TODO add proper variable
        navigate('/home')
    }

    return (
        <>
            <h1>{t('enrollment')}</h1>

            <p>{t('enter_one_of_the_following_to_identify_user')}</p>

            <textarea
                title={t('enter_username')}
                className='username'
                rows='1'
                value={username}
                onChange={e => setUsername(e.target.value)}
                onKeyDown={handleFieldKeyDown}
                placeholder={t('enter_username')}
            >
            </textarea>

            <textarea
                title={t('enter_email')}
                className='email'
                rows='1'
                value={email}
                onChange={e => setEmail(e.target.value)}
                onKeyDown={handleFieldKeyDown}
                placeholder={t('enter_email')}
            >
            </textarea>

            {/* TODO style */}

            <p>{t('select_role')}</p>
            <select title='roles' className='select' onChange={handleRoleChange}>
                {roleOptions.map(e => <option key={e.key} value={e.key}>{e.value}</option>)};
            </select>

            <div className='button-container'>
                <button type='button' className='button accent' onClick={handleEnrollClick}>{t('enroll')}</button>
            </div>
        </>
    );
};

export default Enrollment;
