import React from 'react';
import useLocalStorage from 'use-local-storage';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

import './App.css'

import Home from './pages/Home';
import Login from './pages/Login';
import Navbar from './components/navbar/Navbar';
import Booking from './features/booking/pages/Booking';
import StoreList from './features/storeList/pages/StoreList';
import { ProtectedRoute } from './utils/ProtectedRoute';
import { isAuthenticatied } from './utils/authenticatoin';

const App = () => {

    const defaultDark = window.matchMedia('(prefers-color-sceme: dark)').matches
    const [theme, setTheme] = useLocalStorage('theme', defaultDark ? 'dark' : 'light')

    const switchTheme = () => {
        const newTheme = theme === 'light' ? 'dark' : 'light';
        setTheme(newTheme);
    }

    const authData = getAuthenticationData();

    return (
        <div className='App' data-theme={theme}>
            <BrowserRouter>
                <Navbar/>
                <Routes>
                    <Route exact path='/' element={<Home/>}/>
                    <Route path='/login' element={<Login/>}/>
                    <Route path='/rent' element={<Booking/>}/>
                    <ProtectedRoute path='/store-list' element={<StoreList/>} roles={['manager', 'admin']} userRoles={authData.roles}/>
                </Routes>
                <button className='dark-theme-button' onClick={switchTheme}>{theme === 'light' ? 'Dark' : 'Light'} mode</button>
            </BrowserRouter>
        </div>
    );
}

export default App;
