export const langData = [
    {
        code: 'de',
        label: 'DE',
    },
    {
        code: 'en',
        label: 'EN',
    },
]


export const getLangDataByCode = (code) => {

    const defaultLang = getLangDataByCode('en'); // in case lang code not recognized, use english as default

    lang = langData.find(lang => {
        return lang.code === code
    });

    return lang !== 'undefined' ? lang : defaultLang;
}
