import React, { useEffect, useState } from 'react';
import { accountItemVersions, menuItems, Roles } from '../../components/navbar/menuData'
import MenuItems from './MenuItems';
import MenuItem from './MenuItem';
import './Navbar.css';
import logo from '../../assets/images/logo.png';
import { USER_DATA } from '../../constants/URIs/UserURIs';
import { getCookie } from '../../services/Cookies';
import { ERR_FETCHING_USER_DATA } from '../../constants/ErrorMessages';
import { useLocation } from 'react-router-dom';

const Navbar = () => {

    // gets the current location (Router) to update the navbar after cahnge of user roles
    const location = useLocation();

    const token = getCookie('token');

    // default role is visitor (not logged in)
    const [userRoles, setUserRoles] = useState([Roles.VISITOR]);
    const [filteredMenuItems, setFilteredMenuItems] = useState([]);
    const [filteredAccountItem, setFilteredAccountItem] = useState(null);

    const fetchUserRoles = () => {
        if (token !== 'undefined') {
            fetch(USER_DATA, {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Token ${token}`,
                }
            })
            .then(response => response.json())
            .then(data => {
                setUserRoles(data.user_flags.map(element => element.flag));
            })
            .catch(error => {
                console.error(ERR_FETCHING_USER_DATA, error);
            });
        } else {
            setUserRoles([Roles.VISITOR]);
        }
    }

    // fetch user roles on first render, if token is present (user is logged in)
    useEffect(() => {
        fetchUserRoles();
    }, [location]);

    useEffect(() => {
        console.log('userRoles:', userRoles);
        let filteredItems = menuItems.filter(item =>
            userRoles.some(role => item.roles.includes(role))
        );
        console.log('filteredItems:', filteredItems);
        setFilteredMenuItems(filteredItems);
        setFilteredAccountItem(getAccountItemByRoles(userRoles));
        console.log('filteredMenuItems:', filteredMenuItems);
    }, [userRoles]);

    const getAccountItemByRoles = (roles) => {
        if (!roles || roles.length === 0) return null; // Return null or a default item if no roles are provided

        // Use a Set for faster lookups
        const rolesSet = new Set(roles);

        // Find the first matching account item configuration
        const matchingAccountItem = accountItemVersions.find(version =>
            version.roles.some(role => rolesSet.has(role))
        );

        // Return the matched item, or provide a default/fallback item for visitors if no match
        return matchingAccountItem || accountItemVersions.find(version => version.roles.includes(Roles.VISITOR));
    };

    return (
        <div className='nav-area'>
            <a href='/' className='logo-container'>
                <img className='logo-img' src={logo} alt='Logo' />
            </a>
            <nav className='nav'>
                <ul className='menus'>
                    {filteredMenuItems.length > 0 ? filteredMenuItems.map((item, index) => (
                        <MenuItems key={index} item={item} />
                    )) : null}
                    {/* <li>
                        <MenuItem className='account-menu-item' item={filteredAccountItem} />
                    </li> */}
                </ul>
            </nav>
        </div>
    );
}

export default Navbar;
