import React, { useState } from 'react';
import Switch from '../../../components/switch/Switch';
import TimePicker from 'react-time-picker';
import 'react-time-picker/dist/TimePicker.css';

import { useTranslation } from 'react-i18next';

const DayRow = ({ day }) => {

    const { t } = useTranslation();

    const [isOpen, setIsOpen] = useState(false);
    // const [fromTime, setFromTime] = useState(t('default_from_time'));
    // const [toTime, setToTime] = useState(t('default_to_time'));
    const [fromTime, setFromTime] = useState('10:00');
    const [toTime, setToTime] = useState('13:00');


    const timePickerStyles = {
        border: isOpen ? '2px solid #007BFF' : '1px solid #ccc',
    };

    return (
        <div className="day-row">
            <span className="day-label">{day}</span>
            <Switch className='toggle' id={day} isOn={isOpen} handleToggle={() => setIsOpen(!isOpen)} />
            <TimePicker
                id={day}
                onChange={setFromTime}
                value={fromTime}
                disabled={!isOpen}
                style={timePickerStyles}
            />
            <TimePicker
                id={day}
                onChange={setToTime}
                value={toTime}
                disabled={!isOpen}
                style={timePickerStyles}
            />
        </div>
    );
};

export default DayRow;
