import React, { useEffect, useReducer, useState } from 'react';
import MenuItems from './MenuItems';
import './Navbar.css';
import logo from '../../assets/images/logo.png';
import { USER_DATA } from '../../constants/URIs/UserURIs';
import { getCookie } from '../../services/Cookies';
import { ERR_FETCHING_USER_FLAGS } from '../../constants/ErrorMessages';
import { useLocation } from 'react-router-dom';
import { FaUser } from 'react-icons/fa';
import { ACCOUNT_DELETION, ALL_BIKES, LOGIN, LOGOUT, REGIONAL_BOOKING, REGISTER, STORE_CONFIG } from '../../constants/URLs/Navigation';
import { REGION_NAME, STORE_NAME } from '../../constants/URLs/General';
import { Roles } from '../../constants/Roles';
import { useTranslation } from 'react-i18next';

const Navbar = () => {
    const { t } = useTranslation();
    const location = useLocation();

    // dummy variable to change, when user data in cookies change, to notify the Navbar component to update
    // const [update, setUpdate] = useState(false);

    const [userRoles, setUserRoles] = useState([Roles.VISITOR]);
    const [userStores, setUserStores] = useState([]);
    const [filteredMenuItems, setFilteredMenuItems] = useState([]);

    const [, forceUpdate] = useReducer(x => x + 1, 0);

    useEffect(() => {
        forceUpdate(); // Triggers a rerender
    }, []);

    // fetches user data from cookies
    const fetchUserData = () => {
        const roles = getCookie('userRoles');
        const stores = getCookie('userStores');
        setUserRoles(roles ? roles.split(',') : [Roles.VISITOR]);
        setUserStores(stores ? stores.split(',') : []);
    };

    // Get menu items based on roles and stores
    const getMenuItems = () => {
        const storeItems = userStores.map(store => ({
            title: store,
            url: STORE_CONFIG.replace(STORE_NAME, store),
        }));

        return [
            {
                title: t('booking'),
                url: '/booking',
                roles: [Roles.CUSTOMER, Roles.MANAGER, Roles.ADMINISTRATOR, Roles.VISITOR],
                submenu: [
                    {
                        title: 'Karlsruhe',
                        url: REGIONAL_BOOKING.replace(REGION_NAME, 'karlsruhe'),
                    },
                    {
                        title: 'Ettlingen',
                        url: REGIONAL_BOOKING.replace(REGION_NAME, 'ettlingen'),
                    },
                ],
            },
            {
                title: t('my_stores'),
                roles: [Roles.MANAGER, Roles.ADMINISTRATOR],
                submenu: storeItems,
            },
            {
                title: t('admin_activities'),
                roles: [Roles.ADMINISTRATOR],
                submenu: [
                    { title: t('bookings'), url: '/bookings' },
                    { title: t('users'), url: '/users' },
                ],
            },
            {
                title: <FaUser />,
                roles: [Roles.CUSTOMER, Roles.MANAGER, Roles.ADMINISTRATOR],
                submenu: [
                    { title: t('logout'), url: LOGOUT },
                    { title: t('delete_account'), url: ACCOUNT_DELETION },
                ],
            },
            {
                title: <FaUser />,
                roles: [Roles.VISITOR],
                submenu: [
                    { title: t('login'), url: LOGIN },
                    { title: t('register'), url: REGISTER },
                ],
            },
        ].filter(item => userRoles.some(role => item.roles.includes(role)));
    };

    // Fetch user data when location changes or on initial load
    useEffect(() => {
        fetchUserData();
    }, [location]);

    // Update menu items when user roles or stores change
    useEffect(() => {
        setFilteredMenuItems(getMenuItems());
    }, [userRoles, userStores]);

    return (
        <div className='nav-area'>
            <a href='/' className='logo-container'>
                <img className='logo-img' src={logo} alt='Logo' />
            </a>
            <nav className='nav'>
                <ul className='menus'>
                    {filteredMenuItems.map((item, index) => (
                        <MenuItems key={index} item={item} />
                    ))}
                </ul>
            </nav>
        </div>
    );
};

export default Navbar;
