import React, { useEffect, useState } from 'react';
import { useTranslation } from 'react-i18next';

import '../../../components/lists/List.css';
import BikeList from '../components/BikeList';
import '../components/BikeList.css'
import { ALL_BIKES } from '../../../constants/URIs/AdminURIs';
import { getCookie } from '../../../services/Cookies';
import BikeListItem from '../components/BikeListItem';
import { FaSortAlphaDown, FaSortAlphaUp } from 'react-icons/fa';

// AllBikesPage component definition
const AllBikesPage = () => {

    // Hook for translation
    const { t } = useTranslation();

    const token = getCookie('token');

    const [bikes, setBikes] = useState([]);

    const fetchBikes = async () => {
        const response = await fetch(ALL_BIKES,
            {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Token ${token}`
                }
            }
        );
        const data = await response.json();
        setBikes(data);
    };

    useEffect(() => {
        fetchBikes();
    }, []);

    return (
        <>
            {/* Page title */}
            <h1>{t('all_bikes_in_system')}</h1>

            <div className='list-button-container'>
                <button type='button' className='sort-button' onClick={handleSortClick}>
                    {sortAZ ? <FaSortAlphaUp /> : <FaSortAlphaDown />}
                </button>
                <button type='button' className='new-bike-button' onClick={handleNewBikeClick}>{t('register_new_bike')}</button>
            </div>

            <ul className='list'>
                {bikes.map((bike) => (
                    <BikeListItem bike={bike} key={bike.id} />
                ))}
            </ul>
        </>
    );
};

// Export the BikeListPage component as default
export default AllBikesPage;
